                foreach ($data->urls as $u) {
                    if ($u->type === 'other') {
                        $website = $u->value;
                        break;
                    }
                }
            }
            if (isset($data->gender)) {
                $gender = $data->gender;
            }
            //return new JsonResponse();
        } else if (isset($data->platform) && $data->platform === 'facebook') {
            $facebook_auth_token = substr(explode('.', $postdata->signedRequest)[1], 0, 50);
