                    $isAlreadyExists = in_array($email, $arrEmails);
                    $q->field("isAlreadyExists")->set($isAlreadyExists);
                    if ($isAlreadyExists) {
                        $u = $arrUsers[$email];
                        unset($u['email']);
                        if (!array_key_exists('inTouchCounter', $u)) {
                            $u['inTouchCounter'] = 0;
                        }
                        $q->field("existingUser")->set($u);
                    }
                    $q->field("provider")->set("file");
                    $q->field("createdDate")->set($time);
                    $q->getQuery()->execute();
                    $dm->flush();
