                    $countContacts++;
                }
            }
        }

        $inBusinessRelationCounter = $dm->createQueryBuilder('DataBaseBundle:Invitation')
                ->field('isAlreadyExists')->equals(true)
                ->field('userID')->equals($user->getId())
                ->getQuery()
                ->execute()
                ->count();

        $dm->createQueryBuilder('DataBaseBundle:User')
