          )
          );
          }

          if ($arrQueryCompanies) {
          $q->field('companyPage.employee.company')->in($arrQueryCompanies);
          } */

        if ($arrQueries) {
            /* $q->addAnd(
              $q->expr()->addOr(
              $q->expr()->field('search')->all($arrQueries)
              )->addOr(
              $q->expr()->field('keywords')->all($arrQueries)
              )->addOr(
              $q->expr()->field('competences')->all($arrQueries)
              )->addOr(
