                exec('/usr/bin/convert ' . getcwd() . $copypath . $originalFilename . " -resize '1200x>' -quality 40 " . getcwd() . $copypath . $imageFilename);

                $image = $realpath . $imageFilename;
            }

            $newsImage = null;
