// LINK IMAGE
                $isImage = false;
                $dataURI = $newPost->newsImage;
                $extension = pathinfo($dataURI, PATHINFO_EXTENSION); //$extension will be gif, png,...
                if (!$extension) {
                    foreach ($availableExtensions as $ext) {
