        $q->addOr($q->expr()
                        ->field("visibility")->equals('Everyone')
        );
        $q = $q->sort('_id', -1)
                ->getQuery()
                ->execute();

        foreach ($q as $o) {
            array_push($offers, $o);
        }
        return new JsonResponse($offers);
    }

