        /*
          $numberOdRepliesInWeek = $dm->createQueryBuilder('DataBaseBundle:OfferReply')
          ->field('lastUpdate')->gt($last7d)
          ->getQuery()
          ->execute()->count();
          $response['week_offer_replies'] = $numberOdRepliesInWeek;
         */

